import React from "react";
import styled from "styled-components";
import { users } from "../data/users";
import io from "socket.io-client";
import Logo from "../assets/chaty-logos_white.png";
import { AiOutlineSend, AiOutlinePoweroff } from "react-icons/ai";

const socket = io.connect("http://localhost:5002");

const joinRoom = (roomCode) => {
  socket.emit("join_room", roomCode);
};

const Chat = () => {
  return (
    <Container>
      <ChatContainer>
        <ContactsContainer>
          <Top>
            <Image src={Logo} />
            <Title>Chatty</Title>
          </Top>
          <Bottom>
            {users.map((user) => {
              const userRoomCode = user.roomCode;
              return (
                <Contact
                  key={user.roomCode}
                  onClick={() => joinRoom(userRoomCode)}
                >
                  <ProfileImage src={user.profileImage} />
                  <Username>{user.username}</Username>
                </Contact>
              );
            })}
          </Bottom>
        </ContactsContainer>
        <MessageContainer>
          <MessageTop>
            
            <MessageTopText>Online: </MessageTopText>
            <OnlineBubble />
          </MessageTop>
          <MessageMiddle></MessageMiddle>
          <MessageBottom>
            <Input />
            <SendButton>
              <AiOutlineSend style={{ color: "black" }} />
            </SendButton>
          </MessageBottom>
        </MessageContainer>
      </ChatContainer>
    </Container>
  );
};

const Container = styled.div`
  max-width: 100vw;
  height: 100vh;
  background: #131224;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ChatContainer = styled.div`
  width: 90%;
  height: 90%;
  background: #080421;
  display: flex;
`;

const ContactsContainer = styled.div`
  flex: 1;
  disaplay: flex;
  overflow: auto;
  scrollbar-width: none;
`;

const Top = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Image = styled.img`
  width: 100px;
  height: 100px;
  object-fit: cover;
`;

const Title = styled.h1`
  color: white;
  font-weight: 500;
`;

const Bottom = styled.div`
  flex: 4;
  background: #080421;
  padding: 0px 10px;
`;

const Contact = styled.div`
  height: 80px;
  max-width: 100%;
  background: #3f3b54;
  margin: 10px 0px;
  display: flex;
  align-items: center;
  padding-left: 10px;
  color: white;
  transiton: all 800ms ease-in-out;
  cursor: pointer;
  &:hover {
    background: #595957;
  }
`;

const ProfileImage = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 100%;
  object-fit: cover;
  margin-right: 10px;
`;

const Username = styled.h3`
  font-weight: 400;
`;

const MessageContainer = styled.div`
  flex: 3;
  background: black;
  display: flex;
  flex-direction: column;
`;

const MessageTop = styled.div`
  flex: 1;
  background: #080421;
  display: flex;
  align-items: center;
  justify-content: flex-end;
  padding: 0 10px;
`;

const MessageTopText = styled.p`
  color: white;
  margin-right: 10px;
`;

const OnlineBubble = styled.div`
  width: 10px;
  height: 10px;
  border-radius: 100%;
  background: #22ff00;
`;

const MessageMiddle = styled.div`
  flex: 16;
`;

const MessageBottom = styled.div`
  flex: 2;
  background: #080421;
  display: flex;
`;

const Input = styled.input`
  width: 95%;
  font-weight: bold;
  font-size: 15px;
  padding: 0 10px;
  &: focus {
    outline: none;
  }
`;

const SendButton = styled.div`
  width: 5%;
  background: #03fcc2;
  color: black;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
`;

export default Chat;
