import React from "react";
import styled from "styled-components";
import { users } from "../data/users";
import Logo from "../assets/chaty-logos_white.png";

const Chat = () => {
  return (
    <Container>
      <ChatContainer>
        <ContactsContainer>
          <Top>
            <Image src={Logo} />
            <Title>Chatty</Title>
          </Top>
          <Bottom>
            <Contact></Contact>
          </Bottom>
        </ContactsContainer>
        <MessageContainer></MessageContainer>
      </ChatContainer>
    </Container>
  );
};

const Container = styled.div`
  max-width: 100vw;
  height: 100vh;
  background: #131224;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ChatContainer = styled.div`
  width: 90%;
  height: 90%;
  background: #080421;
  display: flex;
`;

const ContactsContainer = styled.div`
  flex: 1;
  background: teal;
  disaplay: flex;
`;

const Top = styled.div`
  flex: 1;
  background: black;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Image = styled.img`
  width: 100px;
  height: 100px;
  object-fit: cover;
`;

const Title = styled.h1`
  color: white;
  font-weight: 500;
`;

const Bottom = styled.div``;

const Contact = styled.div`
  height: 100px;
`;

const MessageContainer = styled.div`
  flex: 3;
`;

export default Chat;
